import React from 'react'
import * as BooksAPI from './BooksAPI'
import './App.css'
import {Route } from "react-router-dom";
import Search from './components/search'
import Shelves from './components/shelves'


class BooksApp extends React.Component {
  state = {
    books:[]
  }
  componentDidMount(){
BooksAPI.getAll().then(res=>{
  this.setState({books:res})
console.log(res)
})
  }
  updateBookPlace=(book,shelf)=>{
    BooksAPI.update(book,shelf).then(   BooksAPI.getAll().then(res=>{this.setState({books:res})}))
    
  }

  render() {
    BooksAPI.getAll().then(res=>{this.setState({books:res})})
    return (
      <div className="app">
        
        {/* the main page */}
        <Route exact path="/" render={()=>(
          <Shelves
          books={this.state.books}
        changeShelf={this.updateBookPlace}
          />
        )}/>
       {/* the search page */}
<Route path="/search" render={()=>(
          <Search changeShelf={this.updateBookPlace}
          books={this.state.books}
          />
        )}/>
      </div>
    )
  }
}

export default BooksApp
