import React,{Component} from 'react'
import {Link } from "react-router-dom";
import Book from './shelfbook'
class Shelves extends Component{
    render(){
      const {books}=this.props

        return(
                
                <div className="list-books">
                  <div className="list-books-title">
                    <h1>MyReads</h1>
                  </div>
                  <div className="list-books-content">
                    <div>
                      <div className="bookshelf">
                        <h2 className="bookshelf-title">Currently Reading</h2>
                        <div className="bookshelf-books">
                          <ol className="books-grid">
                            {books.filter(books=>books.shelf=== "currentlyReading")
                            .map(book=>{
                            return(
                            <li key={book.id}><Book
                            thisbook={book}
                            changeShelf={this.props.changeShelf}
                            />
                            </li>)})
                            }
                            
                          </ol>
                        </div>
                      </div>
                      <div className="bookshelf">
                        <h2 className="bookshelf-title">Want to Read</h2>
                        <div className="bookshelf-books">
                          <ol className="books-grid">
                          {books.filter(books=>books.shelf==="wantToRead")
                            .map(book=>{
                            return(
                            <li key={book.id}><Book
                            thisbook={book}
                            changeShelf={this.props.changeShelf}
                            /></li>)})
                            }
                          </ol>
                        </div>
                      </div>
                      <div className="bookshelf">
                        <h2 className="bookshelf-title">Read</h2>
                        <div className="bookshelf-books">
                          <ol className="books-grid">
                          {books.filter(books=>books.shelf=== "read")
                            .map(book=>{
                            return(
                            <li key={book.id}><Book
                            thisbook={book}
                            changeShelf={this.props.changeShelf}
                            /></li>)})
                            }
                          </ol>
                        </div>
                      </div>
                    </div>
                  </div>
                  <div className="open-search">
                    <button><Link to="/search" className="d-grid gap-2">Add a book</Link></button>
                  </div>
                </div>
         
        )
    }
}
export default Shelves
